    def test_glove_hotflip(self, lstm, glove_tokenizer):
        # these things are needed for hotflip: pad_token_id, oov_token_id, convert_id_to_word
        assert glove_tokenizer.pad_token_id == 400_000
        assert glove_tokenizer.oov_token_id == 399_999
        assert glove_tokenizer.convert_id_to_word(2_179) == "jack"
        assert 2_179 == lstm.word2id["jack"]

